import React, {useState, useEffect} from  "react";
import {Link} from "react-router-dom";
import axios from "axios";
import {useSelector, useDispatch} from "react-redux";
import history from '../../history';


const StructureRiderHome = (props) => {

    const [ordertwo, setOrdertwo] = useState("");

    const dispatch = useDispatch();
    const { auth, errors } = useSelector( state => ({
    auth: state.auth,
    errors: state.errors,
    }));  

    const data = props.info;

    const changeStatus = (event) => {
       setOrdertwo(event.target.innerText);
       alert ('Now click "Accept Order"');
        console.log(data);
    }

    const SubmitOrder = () => {

        axios.put("/api/cityrunner/riderupdatestatustopending", data)
        .then(() => {
            console.log(`status update: to "pending", id of: ${data._id}`);

        }).catch((error) => {
            console.log(error);
        });

       axios.put(`/api/cityrunner/riderupdaterider/${auth.user.id}`, data)
        .then(() => {
            console.log(`rider update for ${auth.user.id}`);
            history.push("/riderdashboardpending");
        }).catch((error) => {
            console.log(error);
        });

        console.log("done");

       }

    return (
        <tr>     
           <td onClick = {SubmitOrder}>
                {props.id}
            </td>
            <td>
                {props.user}
            </td>
            <td>
                {props.pickupitem} 
            </td>
            <td>
                {props.pickupaddress}  
            </td>
            <td>
                {props.name}
            </td>
            <td>
                {props.phone}
            </td>
            <td>
                {props.email}
            </td>
            <td>
                {props.pickupdate}
            </td>
            <td>
               {props.amountInNaira} 
            </td>
            <td>
               {props.weightinkg} 
            </td>
            <td>
                {props.pickupquantity}
            </td>
            <td>
                {props.deliveryaddress}
            </td>
            <td>
                {props.deliveryname}
            </td>
            <td>
                {props.deliveryphonenumber}
            </td>
            <td>
                {props.deliveryemail}
            </td>
            <td>
                {props.status}
            </td> 
        </tr>
    );

}

export default StructureRiderHome;
