import React, { useState } from 'react';
import { animated, useSpring, config } from 'react-spring';
import Logo from "../../assets/Logo.png";
import Globe from "../../assets/globe.svg";
import Search from "../../assets/magnifying-glass.svg";
import {Link} from "react-router-dom";

const NavBar = () => {
  const [active, setActive] = useState('Home');
  const [open, setOpen] = useState(false);

  const props = useSpring({
    opacity: open ? 1 : 0,
    zIndex: open ? 1 : -1,
    minHeight: open ? '50%' : '0%',
    config: { ...config.molasses, duration: 450 },
  });

  return (
    <div className='sticky'>
      <header className='header'>
          <a href="/"><img src={Logo} className= "logo" alt="logo"/></a>
        <nav>
          <ul className='nav-links'>
            <li className='nav-item'>
              <Link
                to='/home'
                onClick={() => setActive('home')}
                className={active === 'home' ? 'active' : null}
              >
                HOME
              </Link>
            </li>
            <li className='nav-item'>
              <a
                href='#servicesId'
                onClick={() => setActive('about-us')}
                className={active === 'about-us' ? 'active' : null}
              >
                ABOUT US
              </a>
            </li>
            <li className='nav-item'>
              <Link
                to='/sign-in'
                onClick={() => setActive('sign-in')}
                className={active === 'sign-in' ? 'active' : null}
              >
                SIGN IN
              </Link>
            </li>
            <li className='nav-item cont'>
              <Link
                to='/sign-up'
                onClick={() => setActive('sign-up')}
                className={active === 'sign-up' ? 'active' : null}
              >
                SIGN UP
              </Link>
            </li>

          </ul>
          <div onClick={() => setOpen(!open)} className='hamburger'>
            <div className={open ? 'hamburger-icon none' : 'hamburger-icon'}>
              <span className="cross"></span>
              <span className="cross"></span>
            </div>
          </div>
        </nav>
        <animated.div style={props} className='menubar'>
          <ul className='menu-links'>
            <li className='nav-item dropped'>
              <Link
                to='/home'
                onClick={() => setActive('home')}
                className={active === 'home' ? 'active' : null}
              >
                HOME
              </Link>
            </li>
            <li className='nav-item dropped'>
              <a
                href='/#servicesId'
                onClick={() => setActive('about-us')}
                className={active === 'about-us' ? 'active' : null}
              >
                ABOUT US
              </a>
            </li>
            <li className='nav-item dropped'>
              <Link
                to='/sign-in'
                onClick={() => setActive('sign-in')}
                className={active === 'sign-in' ? 'active' : null}
              >
                SIGN IN
              </Link>
            </li>
            <li className='nav-item dropped'>
              <Link
                to='/sign-up'
                onClick={() => setActive('sign-up')}
                className={active === 'sign-up' ? 'active' : null}
              >
                SIGN UP
              </Link>
            </li>

          </ul>
        </animated.div>

      </header>
    </div>
  );
};

export default NavBar;
