    const [ordertwo, setOrdertwo] = useState("");

    const dispatch = useDispatch();
    const { auth, errors } = useSelector( state => ({
    auth: state.auth,
    errors: state.errors,
    }));  

    const changeStatus = (event) => {
       // const target = event.target || event.srcElement;
