import React, {useState, useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {loginRider, setCurrentUser} from "../../actions/authActions";
import {Link} from "react-router-dom";
import backArrow from "../../assets/backarrow.svg";
import NavBar from "../Layout/NavBar";
import Logo from "../../assets/Logo.png";
import classnames from "classnames";
import history from '../../history';



const SignInRider = () => {


    const [userData, setUserData] = useState({
        email: "",
        password: "",
        errors:{},
    });

    const dispatch = useDispatch();

    const { auth, errors } = useSelector(state => ({
      auth: state.auth,
      errors: state.errors,
    })); 

    useEffect(()=> {
      if(auth.isAuthenticated){
        history.push("/riderdashboardhome");
    }

    });

        const handleChange = (event) => {
            const {name, value} =event.target;

            setUserData(prevData => {
                return {
                    ...prevData, [name]: value
                }
            });

            console.log(userData);
        }

        const handleSubmit = (event) => {
            event.preventDefault();

            if(userData.email && userData.password && userData.errors){
              dispatch(loginRider(userData))

            }
            console.log(userData);

        if(auth.isAuthenticated){
            history.push("/riderdashboardhome");

        }



        }

    return(
        <div>
        <Link to="/home" className="normal"><img className="centre-logo" src={Logo} alt="logo"/></Link>
        <div className ="contactBody">
                <br/>

            <div>

            <Link to="/home" className="normal normalback"><p className="ontop-form ontop-form1"><img className="back-arrow" src={backArrow} alt="back arrow"/> &ensp; &ensp;BACK TO HOME</p></Link>
            <p className="ontop-form ontop-form2"><b>Log in</b> below</p>
            <p className="ontop-form ontop-form3">Not yet a Runner?<Link to="/signuprider" className="normal3"><span className="bluelogin"> Register</span></Link></p>

            </div>
            <div className="contactForm">
            <form id="myForm">
            <div class="input-group">
                <input type="email" name="email" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Email</label>
            </div>

            <div class="input-group">
                <input type="password" name="password" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Password</label>
            </div>
            <br/>

            <div className="buttonPosition">
                <button className="contact-button" type="submit" onClick={handleSubmit}> Sign In </button>

                <br/>
                <br/>
              </div>
            </form>
        </div>

        </div>
        </div>
    )
}

export default SignInRider;
