import React, {useState, useEffect} from  "react";
import {Link} from "react-router-dom";
import axios from "axios";
import {useSelector, useDispatch} from "react-redux";
import history from '../../history';


const StructureRiderHome = (props) => {

    const [ordertwo, setOrdertwo] = useState("");

    const dispatch = useDispatch();
    const { auth, errors } = useSelector( state => ({
    auth: state.auth,
    errors: state.errors,
    }));  

    const changeStatus = (event) => {
        alert(event.target.innerText);
        alert(auth.user.id);


        setOrdertwo(event.target.innerText);

    }

    useEffect(() => {

        alert("useeffect");

        alert(ordertwo);


        axios.put("/api/cityrunner/riderupdatestatustopending", ordertwo)
        .then(() => {
            alert(`status update: to pending,value:${ordertwo}`);
        }).catch((error) => {
            console.log(error);
        });

        axios.put(`/api/cityrunner/riderupdaterider/${auth.user.id}`, ordertwo)
        .then(() => {
            alert(`rider update for ${auth.user.id}`);
            history.push("/riderdashboardpending");
        }).catch((error) => {
            console.log(error);
        });

    }, [ordertwo]);

    return (
        <tr>
           <td onClick = {changeStatus}>
                {props.id}
            </td>
            <td>
                {props.user}
            </td>
            <td>
                {props.pickupitem} 
            </td>
            <td>
                {props.pickupaddress}  
            </td>
            <td>
                {props.name}
            </td>
            <td>
                {props.phone}
            </td>
            <td>
                {props.email}
            </td>
            <td>
                {props.pickupdate}
            </td>
            <td>
               {props.amountInNaira} 
            </td>
            <td>
               {props.weightinkg} 
            </td>
            <td>
                {props.pickupquantity}
            </td>
            <td>
                {props.deliveryaddress}
            </td>
            <td>
                {props.deliveryname}
            </td>
            <td>
                {props.deliveryphonenumber}
            </td>
            <td>
                {props.deliveryemail}
            </td>
            <td>
                {props.status}
            </td> 
        </tr>
    );

}

export default StructureRiderHome;
