import React, {useState, useEffect} from  "react";
import UserDashboardNavBar from "./UserDashboardNavBar";
import {useSelector, useDispatch} from "react-redux";
import axios from "axios";
import StructureUser from "./StructureUser";
import Pagination from "react-js-pagination";




const UserDashboardPending = () => {
    const [order, setOrder] = useState([""]);

      /*pagination stuff*/
      const dataPerPage = 20;
      const [ activePage, setCurrentPage ] = useState( 1 );

      const indexOfLastData = activePage * dataPerPage; 
      const indexOfFirstData = indexOfLastData - dataPerPage;
      const currentData = order.slice( indexOfFirstData, indexOfLastData );

      const handlePageChange = ( pageNumber ) => {
          console.log( `active page is ${ pageNumber }` );
          setCurrentPage( pageNumber )
       };

    const dispatch = useDispatch();
    const { auth, errors } = useSelector( state => ({
    auth: state.auth,
    errors: state.errors,
    })); 

    useEffect(() => {

        axios.get(`/api/cityrunner/userpending/${auth.user.id}`)
        .then((res) => {
            setOrder(res.data);
        }).catch(error =>{
            console.log(error); 
        })


    });


    return(
        <div>
        <UserDashboardNavBar /> 
        <div>
        <p className="ontop-form ontop-form2 ontop-dash ontop-dash1"><b>Pending</b> Orders</p>
        </div>
        <div className="table">
                <table>
                    <thead>
                        <tr>
                            <td>
                            Order ID
                            </td>
                            <td>
                            Item
                            </td>
                            <td>
                            Pick-up Address
                            </td>
                            <td>
                            Sender Name
                            </td>
                            <td>
                            Sender Phone Number
                            </td>
                            <td>
                            Sender E-mail
                            </td>
                            <td>
                            Pick-up Date
                            </td>
                            <td>
                            Order Charge
                            </td>
                            <td>
                            Item Weight
                            </td>
                            <td>
                            Item Quantity
                            </td>
                            <td>
                            Recipient Address
                            </td>
                            <td>
                            Recipient Name
                            </td>
                            <td>
                            Recipient Phone Number
                            </td>
                            <td>
                            Recipient E-mail
                            </td>
                            <td>
                            Order Status
                            </td>
                        </tr>
                    </thead>
                    <tbody>
                    {currentData.map((data, index) => {
             return(
                 <StructureUser 
                 key = {index}
                 id= {data._id}
                 pickupitem = {data.pickupitem}
                 pickupaddress = {data.pickupaddress}
                 name= {data.name}
                 phone = {data.phone}
                 email = {data.email}
                 pickupdate = {data.pickupdate}
                 amountInNaira= {data.amountInNaira}
                 weightinkg = {data.weightinkg}
                 pickupquantity = {data.pickupquantity}
                 deliveryaddress = {data.deliveryaddress}
                 deliveryname= {data.deliveryname}
                 deliveryphonenumber = {data.deliveryphonenumber}
                 deliveryemail ={data.deliveryemail}
                 status = {data.status}

                 />);
                })}
                    </tbody>
                </table>

            </div>
        </div>
    )
}

export default UserDashboardPending;
