import React,{useState, useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import backArrow from "../../assets/backarrow.svg";
import NavBar from "../Layout/NavBar";
import Logo from "../../assets/Logo.png";
import {Link} from "react-router-dom";
import {registerUser} from "../../actions/authActions";
import classnames from "classnames";
import history from '../../history';

//FAKE SIGN UP PAGE TO SEND ALL FORM DATA TO EMAIL
const SignUpRider = () => {


    const [state, setState] = useState({
        name:"",
        email: "",
        password: "",
        password2:"",
        role:"",  //ROLE PART NO 1. SET THE ROLE READY IN THE SMALLER STATE IN THE FORM SUBMISSION PLACE
        errors: {}
        });

        const dispatch = useDispatch();
        const { auth, errors } = useSelector(state => ({
          auth: state.auth,
          errors: state.errors,
        })); 

        useEffect(()=> {
    // If logged in and user navigates to Register page, should redirect them to dashboard

            if(auth.isAuthenticated){

                if(auth.user.role==="userrole"){
                    history.push("/userdashboardhome");

                }
                if(auth.user.role==="riderrole"){
                    history.push("riderdashboardhome");
                }

                if(auth.user.role==="adminrole"){
                    history.push("admindashboardhome");
                }

            }

        });

        const handleChange = (event) =>{
            const {name, value} = event.target;

                setState(prevValue => {
                    return {
                        ...prevValue, [name]: value
                    }
                });

                console.log(state);

        };

        const handleSubmit = (event) => {
                event.preventDefault();

                state.role = "userrole"; //ROLE PART NO 2. ASSIGN THE ROLE IN THE FORM SUBMISSION

                if (state.name && state.email && state.password && state.password2 && state.role && state.errors ) {
                  dispatch(registerUser(state))
                 }

        };

    return(
     <div>
     <Link to="/signupuserorrider"><img className="centre-logo" src={Logo} alt="logo"/></Link>

        <div className ="contactBody">
                <br/>

            <div className="form-top">

            <Link to="/home" className="normal normalback"><p className="ontop-form ontop-form1"><img className="back-arrow" src={backArrow} alt="back arrow"/> &ensp; &ensp;BACK TO HOME</p></Link>
            <p className="ontop-form ontop-form2"><b>Register</b> below</p>
            <p className="ontop-form ontop-form3">Already a Runner?<Link to="/signinrider" className="normal3"><span className="bluelogin"> Log in</span></Link></p>



            </div>
            <div className="contactForm">
            <form id="myForm">
            <div class="input-group">
                 <input type="text" name="name" onChange={handleChange} required />
                 <span class="highlight"></span>
                 <span class="bar"></span>
                <label className="label-class">Name</label>
            </div>

            <div class="input-group">
                <input type="email" name="email" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Email</label>
            </div>

            <div class="input-group">
                <input type="tel" name="phonenumber" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Phone Number</label>
            </div>

            <div class="input-group">
                <input type="number" name="age" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Age</label>
            </div>

            <div class="input-group">
                <input type="text" name="address" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Address</label>
            </div>

            <div class="input-group">
                <input type="password" name="password" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Password</label>
            </div>

            <div class="input-group">
                <input type="password" name="password2" onChange={handleChange} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Confirm Pasword</label>
            </div>
            <br/>

            <div className="buttonPosition">
                <button className="contact-button" type="submit" onClick={handleSubmit}> Submit </button>

                <br/>
                <br/>
              </div>
            </form>
        </div>

        </div>
        </div>
    )
}

export default SignUpRider;
