import React from "react";
import axios from "axios";
import {useSelector} from "react-redux";
import history from '../../history';

const StructureRiderDeliveries = (props) => {

    const { auth } = useSelector( state => ({
        auth: state.auth,
        }));  

        const data = props.info;

        const SetDelivered = () => { 


            axios.post("/api/cityrunner/orderdelivered", data) 
            .then((res) => {
                console.log(`Confirmation email sent`);
            }).catch((error) => {
                console.log(error);
            });

            axios.put("/api/cityrunner/deliverydetailsdelivered", data)
            .then(() => {
                console.log(`delivery info set to delivered`);
                history.push('/riderdashboarddeliveries');
                window.location.href="/riderdashboarddeliveries";

            }).catch((error) => {
                console.log(error); 
            });

        }

        const SetPartial = () => {


            axios.post("/api/cityrunner/orderpartialdelivered", data) 
            .then((res) => {
                console.log(`Confirmation email sent`);
            }).catch((error) => {
                console.log(error);
            });

            axios.put("/api/cityrunner/deliverydetailspartial", data)
            .then(() => {
                console.log(`delivery info set to partial`);
                history.push('/riderdashboarddeliveries');
                window.location.href="/riderdashboarddeliveries";


            }).catch((error) => {
                console.log(error); 
            });


        }


        const SetRescheduled = () => {

            axios.post("/api/cityrunner/orderrescheduled", data) 
            .then((res) => {
                console.log(`Confirmation email sent`);
            }).catch((error) => {
                console.log(error);
            });

            axios.put("/api/cityrunner/deliverydetailsrescheduled", data)
            .then(() => {
                console.log(`delivery info set to rescheduled`);
                history.push('/riderdashboarddeliveries');
                window.location.href="/riderdashboarddeliveries";


            }).catch((error) => {
                console.log(error); 
            });

        }

        const SetCancelled = () => {

            axios.post("/api/cityrunner/ordercancelled", data) 
            .then((res) => {
                console.log(`Confirmation email sent`);
            }).catch((error) => {
                console.log(error);
            });

            axios.put("/api/cityrunner/deliverydetailscancelled", data)
            .then(() => {
                console.log(`delivery info set to cancelled`);
                history.push('/riderdashboarddeliveries');
                window.location.href="/riderdashboarddeliveries";


            }).catch((error) => {
                console.log(error); 
            });

        }

        const SetRejected = () => {

            axios.post("/api/cityrunner/orderrejected", data) 
            .then((res) => {
                console.log(`Confirmation email sent`);
            }).catch((error) => {
                console.log(error);
            });

            axios.put("/api/cityrunner/deliverydetailsrejected", data)
            .then(() => {
                console.log(`delivery info set to rejected`);
                history.push('/riderdashboarddeliveries');
                window.location.href="/riderdashboarddeliveries";


            }).catch((error) => {
                console.log(error); 
            });

        }




    return (
        <tr >
           <td > 
                {props.id}
            </td>
            <td>
                {props.user}
            </td>
            <td>
                {props.pickupitem} 
            </td>
            <td>
                {props.pickupaddress}  
            </td>
            <td>
                {props.name}
            </td>
            <td>
                {props.phone}
            </td>
            <td>
                {props.email}
            </td>
            <td>
                {props.pickupdate}
            </td>
            <td>
               {props.amountInNaira} 
            </td>
            <td>
               {props.weightinkg} 
            </td>
            <td>
                {props.pickupquantity}
            </td>
            <td> 
                {props.deliveryaddress}
            </td>
            <td>
                {props.deliveryname}
            </td>
            <td>
                {props.deliveryphonenumber}
            </td>
            <td>
                {props.deliveryemail}
            </td>
            <td>
                {props.status}
            </td> 
            <td>
                {props.deliverydetails}
            </td>  
            <td onClick={SetDelivered} className="orangeandunderline">
                Set order details as Delivered
            </td> 
            <td onClick={SetPartial} className="orangeandunderline">
                Set order details as Partial Delivery
            </td> 
            <td onClick={SetRescheduled} className="orangeandunderline">
                Set order details as Rescheduled
            </td> 
            <td onClick={SetCancelled} className="orangeandunderline">
                Set order details as Cancelled
            </td> 
            <td onClick={SetRejected} className="orangeandunderline">
                Set order details as Rejected
            </td> 
        </tr>
    );

}

export default StructureRiderDeliveries;
