import React, {useState, useEffect} from  "react";
import RiderDashboardNavBar from "./RiderDashboardNavBar";
import StructureRiderHome from "./StructureRiderHome";
import axios from "axios";
import {useSelector, useDispatch} from "react-redux";



const RiderDashboardHome = () => {
    const [order, setOrder] = useState([""]);
    const [ordertwo, setOrdertwo] = useState([""]);


    const dispatch = useDispatch();
    const { auth, errors } = useSelector( state => ({
    auth: state.auth,
    errors: state.errors,
    }));  

    useEffect(() => {

        axios.get(`/api/cityrunner/riderreceived`)
        .then((res) => {
            setOrder(res.data);
        }).catch(error =>{
            console.log(error);
        })


    });


    const changeStatus = (event) => {
        setOrdertwo(event.target.innerHTML);

         axios.put("/riderupdatestatustopending", ordertwo)
         .then((res) => {
             console.log(res.data);
             alert("status update: to pending");
         }).catch((error) => {
             console.log(error);
         })
     }

    return(
        <div>
            <RiderDashboardNavBar />
            <div>
            <p className="ontop-form ontop-form2 ontop-dash ontop-dash1"><b>Runner</b> Unnaccepted Deliveries</p>

            </div>
            <div className="table">
                <table>
                    <thead>
                        <tr>
                        <td>
                            Order ID
                            </td>
                            <td>
                            User ID
                            </td>
                            <td>
                            Item
                            </td>
                            <td>
                            Pick-up Address
                            </td>
                            <td>
                            Sender Name
                            </td>
                            <td>
                            Sender Phone Number
                            </td>
                            <td>
                            Sender E-mail
                            </td>
                            <td>
                            Pick-up Date
                            </td>
                            <td>
                            Order Charge
                            </td>
                            <td>
                            Item Weight
                            </td>
                            <td>
                            Item Quantity
                            </td>
                            <td>
                            Recipient Address
                            </td>
                            <td>
                            Recipient Name
                            </td>
                            <td>
                            Recipient Phone Number
                            </td>
                            <td>
                            Recipient E-mail
                            </td>
                            <td>
                            Order Status
                            </td>
                        </tr>
                    </thead>
                    <tbody>
                    {order.map((data, index) => {
             return(
                 <StructureRiderHome 
                 key = {index}
                 id= {data._id}
                 user = {data.user}
                 pickupitem = {data.pickupitem}
                 pickupaddress = {data.pickupaddress}
                 name= {data.name}
                 phone = {data.phone}
                 email = {data.email}
                 pickupdate = {data.pickupdate}
                 amountInNaira= {data.amountInNaira}
                 weightinkg = {data.weightinkg}
                 pickupquantity = {data.pickupquantity}
                 deliveryaddress = {data.deliveryaddress}
                 deliveryname= {data.deliveryname}
                 deliveryphonenumber = {data.deliveryphonenumber}
                 deliveryemail ={data.deliveryemail}
                 status = {data.status}

                 />);
         })}
                    </tbody>
                </table>

            </div>

        </div>
    )
}

export default RiderDashboardHome;
