import React, {useState, useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {loginUser} from "../../actions/authActions";
import {Link} from "react-router-dom";
import backArrow from "../../assets/backarrow.svg";
import Logo from "../../assets/logo-2.png";
import history from '../../history'; 
import axios from "axios";



const ForgotPassword = () => {
    const [emailErr, setEmailErr] = useState({});

    const [userData, setUserData] = useState({
        email: "",
        errors:{},
    });


    const { auth, errors } = useSelector(state => ({
        auth: state.auth,
        errors: state.errors,
      })); 



      const handleChange = (event) => {
        const {name, value} =event.target;

        setUserData(prevData => {
            return {
                ...prevData, [name]: value
            }
        });

        console.log(userData);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const isValid = formValidation();


        if(isValid){
            if(userData.email && userData.errors){
                Axios.post("/api/cityrunner/forgotpassword")


                setEmailErr("");

            }



        }



        console.log(userData);





    }

    const formValidation = () => {
        const emailErr = {};
        let isValid = true;

        if( typeof userData.email !== "string"){
            emailErr.emailType = "Enter a valid email address";
            isValid = false;
        }

        if( userData.email === ""){
            emailErr.emailRequired = "The email field is required";
            isValid = false;
        }


        setEmailErr(emailErr);
        return isValid;


    }





    return(
            <div>
            <Link to="/signinuserorrider" ><img className="centre-logo" src={Logo} alt="logo"/></Link> 
            <br/>


            <div className="contactForm">
            <form id="myForm">
            <p>Please enter your email below. We will send a temporary password to your mail which you will use to access your account and set a new password</p>
            <div class="input-group">
                <input type="email" name="email" onChange={handleChange} value={userData.email} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Email</label>
            </div>
            {Object.keys(emailErr).map((key)=> {
                return <div style={{color: "red", textAlign: "left"}}> {emailErr[key]} </div>
            })}

            <br/>
            <div className="buttonPosition">
                <button className="contact-button" type="submit" onClick={handleSubmit}> Send Mail </button>

                <br/>
                <br/>
                <p className="forgotpassword">Forgot password?</p>
              </div>
            </form>
        </div>




            </div>

    )

}

export default ForgotPassword;
