import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import {useSelector, useDispatch} from "react-redux";
import {connect} from "react-redux";



const PrivateRouteUser = ({ component: Component, ...rest }) => (

    <Route {...rest} render={props => {
        const dispatch = useDispatch();
        const { auth, errors } = useSelector( state => ({
        auth: state.auth,
        errors: state.errors,
        })); 


        localStorage.getItem('jwtToken') && auth.user.role === "userrole" ? <Component {...props} /> 
        : <Redirect to={{ pathname: '/signinuser', state: { from: props.location } }} />
    }} />




)


const mapStateToProps = state => ({
    auth: state.auth,
  })

  export default connect(mapStateToProps, null)(PrivateRouteUser);



