    const [deliveryphonenumber, setDeliveryphonenumber] = useState("");
    const [deliveryemail, setDeliveryemail] = useState("");

    const Amount = ((pickupweight * pickupquantity)  + pickupoutskirt + deliveryoutskirt);

    const category = "Order Details";

    const data = {
        category, pickupaddress, pickupoutskirt, pickupname, pickupphonenumber, pickupemail, pickupdate,
        pickupitem, pickupweight, pickupquantity, deliveryaddress, deliveryoutskirt, deliveryname, 
        deliveryphonenumber, deliveryemail
    };

    const componentProps = {
        pickupemail,
        amount,
        metadata: {
          pickupname,
          pickupphone,
        },
        publicKey,
        text: "Pay Now",
        onSuccess: () => {
          axios.post("/api/lohi/product", data)
          .then((res) => {
              console.log(res.data);
              alert("Thanks for doing business with us! Come back soon!! \n Confirmation email sent");
              window.location.href = "/onPaymentSuccess"
          }).catch((error) => {
              console.log(error);
          })
        },
        onClose: () => alert("Wait! Don't leave :("),
      }

