                 <span class="bar"></span>
                <label className="label-class">Delivery Address</label>
            </div>

            <div class="input-group">
                 <input type="text" id="deliverystate" name="deliverystate" value="Lagos" required />
                 <span class="highlight"></span>
                 <span class="bar"></span>
                 <label className="label-class">State</label>
            </div>

            <div class="input-group outskirt">
            <label className="outskirt" style={{textAlign: "left"}}>Is the delivery address at the outskirts of Lagos? e.g after Ojodu Beggar(Arepo, Mowe)/ beyond Ajah(Epe)</label>

            </div>

            <div className="radio-group">
            <input type="radio" id="yesdeliveryoutskirt" name="deliveryoutskirt" value="yesdeliveryoutskirt" 
            checked={deliveryoutskirt === 300} onClick={(e) => setDeliveryoutskirt(300)}/>
            <label htmlFor="delivery" className="radio-label">Yes, at outskirts</label>
            <br/>
            <input type="radio" id="nodeliveryoutskirt" name="deliveryoutskirt" value="nodeliveryoutskirt"
            checked={deliveryoutskirt === 0} onClick={(e) => setDeliveryoutskirt(0)}/>
            <label htmlFor="pick-up" className="radio-label">No, within Lagos</label>

            </div>

            <div class="input-group">
                <input type="text" id="deliveryname" name="deliveryname" onChange={(e)=>setDeliveryname(e.target.value)} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Name</label>
            </div>

            <div class="input-group">
                <input type="tel" id="deliveryphonenumber" name="deliveryphonenumber" onChange={(e)=>setDeliveryphonenumber(e.target.value)} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Phone Number</label>
            </div>

            <div class="input-group">
                <input type="email" id="deliveryemail" name="deliveryemail" onChange={(e)=>setDeliveryemail(e.target.value)} required />
                <span class="highlight"></span>
                <span class="bar"></span>
                <label className="label-class">Email</label>
            </div>




            </form>
            <div className="buttonPosition">
            <PaystackButton {...componentProps} className="contact-button"/>
                <br/>
                <br/>
              </div>
        </div>

        </div>

        </div>
    )
}

export default UserDashboardHome;
