import React, {useState, useEffect} from "react";
import {useSelector, useDispatch} from "react-redux";
import {Link} from "react-router-dom";
import backArrow from "../../assets/backarrow.svg";
import NavBar from "../Layout/NavBar";
import history from '../../history';


const SignInUserOrRider = () => {

    const dispatch = useDispatch();

    const { auth, errors } = useSelector(state => ({
      auth: state.auth,
      errors: state.errors,
    })); 

    useEffect(()=> {
        if(auth.isAuthenticated){

            if(auth.user.role==="userrole"){
                history.push("/userdashboardhome");

            }
            if(auth.user.role==="riderrole"){
                history.push("riderdashboardhome");
            }

            if(auth.user.role==="adminrole"){
                history.push("admindashboardhome");
            }

        }

    });
    return(
        <div>
        <NavBar />
        <div>

        </div>
        <div  className="options-div">
            <Link to="/signinuser"><p className="options">Sign In as a User</p></Link>
            <p className="options"><b>OR</b></p>
            <Link to="/signinrider"><p className="options">Sign In as a Runner</p></Link>
        </div>
        </div>
    )
}

export default SignInUserOrRider;
