import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import {useSelector, useDispatch} from "react-redux";



const PrivateRouteRider = ({ component: Component, ...rest }) => {
    const dispatch = useDispatch();
    const { auth } = useSelector( state => ({
    auth: state.auth,
    })); 

   return(
        <Route {...rest} render={props => (
        localStorage.getItem('jwtToken') && auth.user.role === "riderrole" ? <Component {...props} /> 
        : <Redirect to={{ pathname: '/signinrider', state: { from: props.location } }} />
        )} />
   )

}


export default PrivateRouteRider;
