import React, {useState, useEffect} from  "react";
import RiderDashboardNavBar from "./RiderDashboardNavBar";
import StructureRiderPending from "./StructureRiderPending";
import axios from "axios";
import {useSelector, useDispatch} from "react-redux";


const RiderDashboardPending = () => {
const [order, setOrder] = useState([""]);


const dispatch = useDispatch();
const { auth, errors } = useSelector( state => ({
auth: state.auth,
errors: state.errors,
}));  

useEffect(() => {

    let isActive = true;

    if (isActive){
        axios.get(`/api/cityrunner/riderpending/${auth.user.id}`)
        .then((res) => {
        setOrder(res.data);

    }).catch(error =>{
        console.log(error);
    })

    }
    return () => {
        isActive = false;
      };


},[]);


    return(
        <div>
            <RiderDashboardNavBar />
            <div>
            <p className="ontop-form ontop-form2 ontop-dash ontop-dash1"><b>Accepted Pending</b> Deliveries</p>

            </div>
            <div className="table">
                <table>
                    <thead>
                        <tr>
                        <td>
                            Order ID
                            </td>
                            <td>
                            User ID
                            </td>
                            <td>
                            Item
                            </td>
                            <td>
                            Pick-up Address
                            </td>
                            <td>
                            Sender Name
                            </td>
                            <td>
                            Sender Phone Number
                            </td>
                            <td>
                            Sender E-mail
                            </td>
                            <td>
                            Pick-up Date
                            </td>
                            <td>
                            Order Charge
                            </td>
                            <td>
                            Item Weight
                            </td>
                            <td>
                            Item Quantity
                            </td>
                            <td>
                            Recipient Address
                            </td>
                            <td>
                            Recipient Name
                            </td>
                            <td>
                            Recipient Phone Number
                            </td>
                            <td>
                            Recipient E-mail
                            </td>
                            <td>
                            Order Status
                            </td>
                        </tr>
                    </thead>
                    <tbody>
            {order.map((data, index) => {
             return(
                 <StructureRiderPending 
                 key = {index}
                 id= {data._id}
                 user = {data.user}
                 pickupitem = {data.pickupitem}
                 pickupaddress = {data.pickupaddress}
                 name= {data.name}
                 phone = {data.phone}
                 email = {data.email}
                 pickupdate = {data.pickupdate}
                 amountInNaira= {data.amountInNaira}
                 weightinkg = {data.weightinkg}
                 pickupquantity = {data.pickupquantity}
                 deliveryaddress = {data.deliveryaddress}
                 deliveryname= {data.deliveryname}
                 deliveryphonenumber = {data.deliveryphonenumber}
                 deliveryemail ={data.deliveryemail}
                 status = {data.status}
                 info = {data} 


                 />);
         })}
                    </tbody>
                </table>

            </div>

        </div>
    )
}

export default RiderDashboardPending;
